export default {
  name: 'App',
  components: {
  },
  data () {
    return {
      transitionName: 'vux-pop-out'
    }
  },
  created () {
  },
  watch: {
    $route (to, from) {
      if (to.path !== '/') {
        this.transitionName = 'vux-pop-in'
      } else {
        this.transitionName = 'vux-pop-out'
      }
    }
  },
  methods: {
    changeLang () {
      const clang = this.$vuetify.lang.current
      this.$vuetify.lang.current = (clang === 'en') ? 'jp' : 'en'
    }
  }
}
