body {
  padding-bottom: constant(safe-area-inset-bottom);
  padding-bottom: env(safe-area-inset-bottom);
}
.theme--light.application:before {
  content: ' ';
  position: fixed;
  z-index: -1;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background: url('./assets/bg_grey.png') center 0 repeat;
  background-size: 20%;
}
.theme--light.application {
  background: none;
}
footer {
  border-top: 1px solid #eee;
}
.v-toolbar > .title {
  font-size: 18px;
}
.v-toolbar button {
  min-width: 60px;
  padding: 5px;
}

.vux-pop-in-enter-active,
.vux-pop-in-leave-active {
    will-change: transform;
    transition: all 500ms;
    height: 100%;
    width: 100%;
    position: absolute;
    backface-visibility: hidden;
    perspective: 1000;
    overflow: hidden;
}
.vux-pop-out-enter {
    opacity: 0;
    transform: translate3d(-100%, 0, 0);
}
.vux-pop-out-leave-active {
  opacity: 0;
  transform: translate3d(100%, 0, 0);
}
.vux-pop-in-enter {
  opacity: 0;
  transform: translate3d(100%, 0, 0);
}
.vux-pop-in-leave-active {
  opacity: 0; transform: translate3d(-100%, 0, 0);
}

.router-view{
  height:100%;
  width: 100%;
  position: absolute;
  -webkit-transition: all .3s cubic-bezier(1,0,.1,1);
  -moz-transition: all .3s cubic-bezier(1,0,.1,1);
  -ms-transition: all .3s cubic-bezier(1,0,.1,1);
  -o-transition: all .3s cubic-bezier(1,0,.1,1);
  transition: all .3s cubic-bezier(1,0,.1,1);
}
